exports.shutDownComputer = function() {
    var operatingSystem = process.platform;

    if (operatingSystem === 'linux' || operatingSystem === 'darwin') {
        const exec = require('child_process').exec;
        const shutdown = exec('poweroff', (error, stdout, stderr) => {
                console.log(`${stdout}`);
                console.log(`${stderr}`);
                if (error !== null) {
                    console.log(`exec error: ${error}`);
                }
            }); 
    } else if (operatingSystem === 'win32') {
        const exec = require('child_process').exec;
        const shutdown = exec('shutdown.exe /s /t 00', (error, stdout, stderr) => {
                console.log(`${stdout}`);
                console.log(`${stderr}`);
                if (error !== null) {
                    console.log(`exec error: ${error}`);
                }
            }); 
    }
} 

